import React, { createContext, useReducer, useEffect } from 'react';
import axios from 'axios';

const CartContext = createContext();

const cartReducer = (state, action) => {
  switch (action.type) {
    case 'SET_CART':
      return {
        ...state,
        cartItems: action.payload,
      };
    case 'ADD_ITEM':
      return {
        ...state,
        cartItems: [...state.cartItems, action.payload],
      };
    case 'REMOVE_ITEM':
      return {
        ...state,
        cartItems: state.cartItems.filter((item) => item.product._id !== action.payload),
      };
    default:
      return state;
  }
};

export const CartProvider = ({ children }) => {
  const [state, dispatch] = useReducer(cartReducer, { cartItems: [] });

  const fetchCart = async () => {
    try {
      const token = localStorage.getItem('token');
      if (token) {
        const config = {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        };
        const { data } = await axios.get('/api/cart', config);
        dispatch({ type: 'SET_CART', payload: data.cartItems });
      }
    } catch (error) {
      console.error('Failed to fetch cart', error);
    }
  };

  useEffect(() => {
    fetchCart();
  }, []);

  const addToCart = async (productId, qty) => {
    try {
        const token = localStorage.getItem('token');
        const config = {
            headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${token}`,
            },
        };
        const { data } = await axios.post('/api/cart', { productId, qty }, config);
        dispatch({ type: 'SET_CART', payload: data.cartItems });
    } catch (error) {
        console.error('Failed to add to cart', error);
    }
  };

  const removeFromCart = async (productId) => {
    try {
        const token = localStorage.getItem('token');
        const config = {
            headers: {
            Authorization: `Bearer ${token}`,
            },
        };
        await axios.delete(`/api/cart/${productId}`, config);
        dispatch({ type: 'REMOVE_ITEM', payload: productId });
    } catch (error) {
        console.error('Failed to remove from cart', error);
    }
  };

  return (
    <CartContext.Provider value={{ cart: state, addToCart, removeFromCart }}>
      {children}
    </CartContext.Provider>
  );
};

export default CartContext;
