import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { loadStripe } from '@stripe/stripe-js';
import CartContext from '../context/CartContext';

const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY);

const CheckoutPage = () => {
  const [address, setAddress] = useState('');
  const [city, setCity] = useState('');
  const [postalCode, setPostalCode] = useState('');
  const [country, setCountry] = useState('');
  const { cart } = useContext(CartContext);
  const { cartItems } = cart;
  const navigate = useNavigate();

  const submitHandler = async (e) => {
    e.preventDefault();
    const orderItems = cartItems.map(item => ({
        _id: item.product._id,
        name: item.product.name,
        qty: item.qty,
        image: item.product.image,
        price: item.product.price,
    }));

    const shippingAddress = { address, city, postalCode, country };
    const itemsPrice = cartItems.reduce((acc, item) => acc + item.qty * item.product.price, 0);
    const taxPrice = 0.15 * itemsPrice;
    const shippingPrice = itemsPrice > 100 ? 0 : 10;
    const totalPrice = itemsPrice + taxPrice + shippingPrice;

    const order = {
        orderItems,
        shippingAddress,
        paymentMethod: 'Stripe',
        itemsPrice,
        taxPrice,
        shippingPrice,
        totalPrice,
    };

    try {
      const token = localStorage.getItem('token');
      const config = {
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
      };
      const { data: createdOrder } = await axios.post('/api/orders', order, config);

      const { data: session } = await axios.post('/api/orders/create-checkout-session', {
          orderItems: createdOrder.orderItems,
          success_url: `${window.location.origin}/order-success`,
          cancel_url: `${window.location.origin}/order-cancel`,
      }, config);

      const stripe = await stripePromise;
      await stripe.redirectToCheckout({ sessionId: session.id });

    } catch (error) {
      console.error('Error during checkout:', error);
    }
  };

  return (
    <form onSubmit={submitHandler}>
      <h2>Shipping</h2>
      <input
        type="text"
        placeholder="Address"
        value={address}
        onChange={(e) => setAddress(e.target.value)}
        required
      />
      <input
        type="text"
        placeholder="City"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        required
      />
      <input
        type="text"
        placeholder="Postal Code"
        value={postalCode}
        onChange={(e) => setPostalCode(e.target.value)}
        required
      />
      <input
        type="text"
        placeholder="Country"
        value={country}
        onChange={(e) => setCountry(e.target.value)}
        required
      />
      <button type="submit">Continue</button>
    </form>
  );
};

export default CheckoutPage;
